const { Command } = require('discord-akairo');

class OgCommand extends Command {
    constructor() {
        super('og*', {
            //if sentence ends with og or trans rights
            regex: /(og|trans rights)$/i
        });
    }

    exec(message, args) {
        message.channel.send(message.content + '!!');
    }
}

module.exports = OgCommand;
