const { Command } = require('discord-akairo');

class PingCommand extends Command {
    constructor() {
        super('ping', {
           aliases: ['ping']
        });
    }

    exec(message) {
		message.channel.send('Pong!');
    	console.log('Pong!');
        return 1;
    }
}

module.exports = PingCommand;
